import React, { useState, useEffect } from "react";
import axios from "axios";
import {
  Container,
  Grid,
  Card,
  CardMedia,
  CardContent,
  Typography,
  Modal,
  Box,
  Button
} from "@mui/material";
import CustomNavbar from "../components/Navbar";
import MovieCarousel from "../components/MovieCarousel";
import SearchBar from "../components/SearchBar";
import { tollywoodMovies, bollywoodMovies, hollywoodMovies } from "../data/movieList";
import "./Home.css";

const Home = () => {
  const [movie, setMovie] = useState(null);
  const [verdict, setVerdict] = useState("Fetching Verdict...");
  const [loading, setLoading] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [searchInput, setSearchInput] = useState("");
  const [tollywoodMoviesData, setTollywoodMoviesData] = useState([]);
  const [bollywoodMoviesData, setBollywoodMoviesData] = useState([]);
  const [hollywoodMoviesData, setHollywoodMoviesData] = useState([]);
  const OMDB_API_KEY = process.env.REACT_APP_OMDB_API_KEY;

  const getRandomMovies = (movies) => {
    return [...movies].sort(() => Math.random() - 0.5).slice(0, 5);
  };

  const fetchMovie = (title) => {
    setLoading(true);
    axios
      .get(`http://127.0.0.1:4121/api/movies/search?title=${title}`)
      .then((res) => {
        setMovie(res.data);
        setVerdict("Fetching Verdict...");
        fetchVerdict(res.data.title);
        setIsModalOpen(true);
        setLoading(false);
      })
      .catch(() => {
        alert("Movie not found!");
        setLoading(false);
      });
  };

  const fetchVerdict = (movieTitle) => {
    axios
      .post("http://127.0.0.1:4121/api/sentiment/analyze", {
        reviews: ["This movie is amazing!", "Loved the storyline!"],
        movie_title: movieTitle,
      })
      .then((res) => setVerdict(res.data.verdict))
      .catch(() => setVerdict("Error fetching verdict"));
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setMovie(null);
    setVerdict("Fetching Verdict...");
    setSearchInput("");
  };

  useEffect(() => {
    const fetchMoviesData = async (movies) => {
      try {
        const randomMovies = getRandomMovies(movies);
        const movieRequests = randomMovies.map((title) =>
          axios.get(`https://www.omdbapi.com/?t=${title}&apikey=${OMDB_API_KEY}`)
        );
        const responses = await Promise.all(movieRequests);
        return responses
          .filter((res) => res.data.Response === "True" && res.data.Poster !== "N/A")
          .map((res) => ({
            title: res.data.Title,
            year: res.data.Year,
            poster: res.data.Poster,
          }));
      } catch {
        return [];
      }
    };

    const loadMovies = async () => {
      setTollywoodMoviesData(await fetchMoviesData(tollywoodMovies));
      setBollywoodMoviesData(await fetchMoviesData(bollywoodMovies));
      setHollywoodMoviesData(await fetchMoviesData(hollywoodMovies));
    };

    loadMovies();
  }, []);

  return (
    <div style={{ backgroundColor: "black", minHeight: "100vh", paddingBottom: "20px" }}>
      <CustomNavbar />
      <MovieCarousel />
      <Container className="mt-3">
        <SearchBar onSearch={fetchMovie} searchInput={searchInput} setSearchInput={setSearchInput} />
      </Container>

      <Container className="text-center mt-3">
        <Typography variant="h4" className="text-gold">
          Where Quality & Clarity Matters
        </Typography>
        <Typography variant="body1" className="text-light">
          Watch Tollywood, Bollywood, Hollywood movies in HD, exclusively available on iBommaFlix.
        </Typography>
      </Container>

      {loading && <div className="spinner"></div>}

      {/* âœ… Movie Card Modal */}
      <Modal open={isModalOpen} onClose={closeModal}>
        <Box className="modal-box">
          {movie ? (
            <>
              <Typography variant="h5">{movie.title} ({movie.year})</Typography>
              <img src={movie.poster} alt={movie.title} className="modal-img" />
              <Typography><strong>Genre:</strong> {movie.genre}</Typography>
              <Typography><strong>IMDb Rating:</strong> {movie.rating}</Typography>
              <Typography><strong>Plot:</strong> {movie.plot}</Typography>
              <Typography variant="h5" className="text-warning">{verdict}</Typography>
              <Button onClick={closeModal} className="close-button">Close</Button>
            </>
          ) : (
            <Typography>Loading movie details...</Typography>
          )}
        </Box>
      </Modal>

      {/* âœ… Movie Sections */}
      <Container className="movie-sections">
        {[{ title: "Tollywood Movies", data: tollywoodMoviesData },
          { title: "Bollywood Movies", data: bollywoodMoviesData },
          { title: "Hollywood Movies", data: hollywoodMoviesData }].map((category, i) => (
          <div key={i} className="movie-category">
            <Typography variant="h4" className="text-gold text-center">{category.title}</Typography>
            <Grid container spacing={2} justifyContent="center">
              {category.data.map((movie, index) => (
                <Grid item key={index} xs={12} sm={6} md={4} lg={2}>
                  <Card className="movie-card">
                    <CardMedia
                        component="img"
                        image={movie.poster}
                        alt={movie.title}
                        className="movie-img"
                        sx={{
                            width: "100%",
                            height: "auto",
                            aspectRatio: "3/3", // âœ… Fixes vertical stretching
                            objectFit: "cover", // âœ… Ensures proper scaling
                            borderRadius: "10px"
                        }}
                    />
                    <CardContent>
                      <Typography className="text-gold">{movie.title}</Typography>
                      <Typography className="text-light">{movie.year}</Typography>
                    </CardContent>
                  </Card>
                </Grid>
              ))}
            </Grid>
          </div>
        ))}
      </Container>
    </div>
  );
};

export default Home;
