import React, { useEffect, useState } from "react";
import axios from "axios";
import { Carousel } from "react-bootstrap"; // ✅ Bootstrap Carousel
import "bootstrap/dist/css/bootstrap.min.css"; // ✅ Ensure Bootstrap is loaded
import "./MovieCarousel.css";
import { tollywoodMovies, bollywoodMovies, hollywoodMovies } from "../data/movieList";

const MovieCarousel = () => {
  const [moviePosters, setMoviePosters] = useState([]);
  const OMDB_API_KEY = process.env.REACT_APP_OMDB_API_KEY; // ✅ Load API key from .env

  useEffect(() => {
    const fetchPosters = async () => {
      if (!OMDB_API_KEY) {
        console.error("OMDB API Key is missing. Check your .env file.");
        return;
      }

      try {
        const getRandomMovies = (movies, count) =>
          movies.sort(() => Math.random() - 0.5).slice(0, count);

        const selectedMovies = [
          ...getRandomMovies(tollywoodMovies, 10),
          ...getRandomMovies(bollywoodMovies, 10),
          ...getRandomMovies(hollywoodMovies, 10)
        ];

        const posterRequests = selectedMovies.map((title) =>
          axios.get(`https://www.omdbapi.com/?t=${title}&apikey=${OMDB_API_KEY}`)
        );

        const responses = await Promise.all(posterRequests);

        const posters = responses
          .filter(res => res.data.Response === "True" && res.data.Poster !== "N/A")
          .map(res => res.data.Poster);

        setMoviePosters(posters);
      } catch (error) {
        console.error("Error fetching movie posters from OMDb:", error);
      }
    };

    fetchPosters();
  }, []);

  return (
    <div className="movie-carousel">
      {moviePosters.length > 0 ? (
        <Carousel controls={false} indicators={false} interval={2000} pause={false} fade={true} data-bs-theme="dark">
          {[...Array(Math.ceil(moviePosters.length / 5))].map((_, i) => (
            <Carousel.Item key={i}>
              <div className="carousel-container">
                {moviePosters.slice(i * 5, i * 5 + 5).map((poster, index) => (
                  <div key={index} className="carousel-movie">
                    <img className="poster-img" src={poster} alt="Movie Poster" />
                  </div>
                ))}
              </div>
            </Carousel.Item>
          ))}
        </Carousel>
      ) : (
        <p className="loading-text">Loading posters...</p>
      )}
    </div>
  );
};

export default MovieCarousel;
