import React, { useState, useEffect } from "react";
import axios from "axios";
import Modal from "react-bootstrap/Modal";
import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Card from "react-bootstrap/Card";
import "bootstrap/dist/css/bootstrap.min.css";
import CustomNavbar from "../components/Navbar";
import MovieCarousel from "../components/MovieCarousel";
import SearchBar from "../components/SearchBar";
import { tollywoodMovies, bollywoodMovies, hollywoodMovies } from "../data/movieList";
import "./Home.css";

const Home = () => {
  const [movie, setMovie] = useState(null);
  const [verdict, setVerdict] = useState("Fetching Verdict...");
  const [loading, setLoading] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [searchInput, setSearchInput] = useState("");
  const [tollywoodMoviesData, setTollywoodMoviesData] = useState([]);
  const [bollywoodMoviesData, setBollywoodMoviesData] = useState([]);
  const [hollywoodMoviesData, setHollywoodMoviesData] = useState([]);
  const OMDB_API_KEY = process.env.REACT_APP_OMDB_API_KEY;

  // ✅ Get 5 Random Movies for Each Industry
  const getRandomMovies = (movies) => {
    const shuffled = [...movies].sort(() => Math.random() - 0.5);
    return shuffled.slice(0, 5);
  };

  const fetchMovie = (title) => {
    setLoading(true);
    axios
      .get(`http://127.0.0.1:4121/api/movies/search?title=${title}`)
      .then((res) => {
        setMovie(res.data);
        setVerdict("Fetching Verdict...");
        fetchVerdict(res.data.title);
        setTimeout(() => setIsModalOpen(true), 200);
        setLoading(false);
      })
      .catch(() => {
        alert("Movie not found!");
        setLoading(false);
      });
  };

  const fetchVerdict = (movieTitle) => {
    axios
      .post("http://127.0.0.1:4121/api/sentiment/analyze", {
        reviews: ["This movie is amazing!", "Loved the storyline!"],
        movie_title: movieTitle,
      })
      .then((res) => setVerdict(res.data.verdict))
      .catch(() => setVerdict("Error fetching verdict"));
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setMovie(null);
    setVerdict("Fetching Verdict...");
    setSearchInput("");
  };

  useEffect(() => {
    const fetchMoviesData = async (movies) => {
      try {
        const randomMovies = getRandomMovies(movies);
        const movieRequests = randomMovies.map((title) =>
          axios.get(`https://www.omdbapi.com/?t=${title}&apikey=${OMDB_API_KEY}`)
        );
        const responses = await Promise.all(movieRequests);
        return responses
          .filter((res) => res.data.Response === "True" && res.data.Poster !== "N/A")
          .map((res) => ({
            title: res.data.Title,
            year: res.data.Year,
            poster: res.data.Poster,
          }));
      } catch {
        return [];
      }
    };

    const loadMovies = async () => {
      setTollywoodMoviesData(await fetchMoviesData(tollywoodMovies));
      setBollywoodMoviesData(await fetchMoviesData(bollywoodMovies));
      setHollywoodMoviesData(await fetchMoviesData(hollywoodMovies));
    };

    loadMovies();
  }, [OMDB_API_KEY]);

  return (
    <div>
      <CustomNavbar />
      <MovieCarousel />
      <Container className="mt-3">
        <SearchBar onSearch={fetchMovie} searchInput={searchInput} setSearchInput={setSearchInput} />
      </Container>

      <Container className="text-center mt-3">
        <h2 className="text-gold">Where Quality & Clarity Matters</h2>
        <p className="text-light">
          Watch Tollywood, Bollywood, Hollywood movies in HD, exclusively available on iBommaFlix.
        </p>
      </Container>

      {loading && <div className="spinner"></div>}

      {/* ✅ Movie Card Popup Modal */}
      <Modal show={isModalOpen} onHide={closeModal} centered>
        <Modal.Header className="modal-header">
          <Modal.Title>Movie Details</Modal.Title>
          <Button className="close-button" onClick={closeModal}>✖</Button>
        </Modal.Header>
        <Modal.Body className="modal-body">
          {movie ? (
            <>
              <h3>{movie.title} ({movie.year})</h3>
              <img src={movie.poster} alt={movie.title} className="img-fluid rounded movie-modal-img" />
              <p><strong>Genre:</strong> {movie.genre}</p>
              <p><strong>IMDb Rating:</strong> {movie.rating}</p>
              <p><strong>Plot:</strong> {movie.plot}</p>
              <h3 className="text-warning">{verdict}</h3>
            </>
          ) : (
            <p>Loading movie details...</p>
          )}
        </Modal.Body>
      </Modal>

      {/* ✅ Movie Sections (Bootstrap Grid - 5 Movies Per Row on Desktop) */}
      <Container className="movie-sections">
        {[{ title: "Tollywood Movies", data: tollywoodMoviesData },
          { title: "Bollywood Movies", data: bollywoodMoviesData },
          { title: "Hollywood Movies", data: hollywoodMoviesData }].map((category, i) => (
          <div key={i} className="mb-5">
            <h2 className="text-gold text-center mb-4">{category.title}</h2>
            <Row className="justify-content-center">
              {category.data.map((movie, index) => (
                <Col key={index} xs={12} sm={6} md={4} lg={2} className="mb-3">
                  <Card className="movie-card">
                    <Card.Img variant="top" src={movie.poster} className="movie-img" />
                    <Card.Body>
                      <Card.Title className="text-gold">{movie.title}</Card.Title>
                      <Card.Text className="text-light">{movie.year}</Card.Text>
                    </Card.Body>
                  </Card>
                </Col>
              ))}
            </Row>
          </div>
        ))}
      </Container>
    </div>
  );
};

export default Home;
