import React, { useState } from "react";
import { TextField, List, ListItem, ListItemText, Button } from "@mui/material";
import axios from "axios";

const SearchBar = ({ onSearch, searchInput, setSearchInput }) => {
  const [suggestions, setSuggestions] = useState([]);
  const [loading, setLoading] = useState(false);

  // Function to fetch movie suggestions
  const fetchSuggestions = async (query) => {
    if (!query) {
      setSuggestions([]);
      return;
    }
    setLoading(true);
    try {
      const res = await axios.get(`https://www.omdbapi.com/?s=${query}&apikey=${process.env.REACT_APP_OMDB_API_KEY}`);
      if (res.data.Response === "True") {
        setSuggestions(res.data.Search);
      } else {
        setSuggestions([]);
      }
    } catch {
      setSuggestions([]);
    }
    setLoading(false);
  };

  // Handle input changes and search suggestions
  const handleInputChange = (event) => {
    const inputValue = event.target.value;
    setSearchInput(inputValue);
    fetchSuggestions(inputValue);
  };

  // Handle suggestion click
  const handleSuggestionClick = (suggestion) => {
    setSearchInput(suggestion.Title);
    setSuggestions([]);
    onSearch(suggestion.Title);
  };

  return (
    <div>
      <TextField
        fullWidth
        label="Search Movie"
        variant="outlined"
        value={searchInput}
        onChange={handleInputChange}
        onBlur={() => setTimeout(() => setSuggestions([]), 200)} // Hide suggestions after focus is lost
      />
      {suggestions.length > 0 && (
        <List sx={{ maxHeight: 200, overflowY: "auto", border: "1px solid #ccc", position: "absolute", zIndex: 10 }}>
          {suggestions.map((movie, index) => (
            <ListItem button key={index} onClick={() => handleSuggestionClick(movie)}>
              <ListItemText primary={movie.Title} />
            </ListItem>
          ))}
        </List>
      )}
    </div>
  );
};

export default SearchBar;
