import React, { useState, useEffect } from "react";
import axios from "axios";
import Modal from "react-bootstrap/Modal"; // âœ… Bootstrap Modal
import Button from "react-bootstrap/Button"; // âœ… Bootstrap Button
import Container from "react-bootstrap/Container"; // âœ… Bootstrap Container
import Row from "react-bootstrap/Row"; // âœ… Bootstrap Grid Row
import Col from "react-bootstrap/Col"; // âœ… Bootstrap Grid Column
import CustomNavbar from "../components/Navbar";
import MovieCarousel from "../components/MovieCarousel";
import SearchBar from "../components/SearchBar";
import { tollywoodMovies, bollywoodMovies, hollywoodMovies } from "../data/movieList";
import "bootstrap/dist/css/bootstrap.min.css"; // âœ… Bootstrap CSS
import "./Home.css";

const Home = () => {
  const [movie, setMovie] = useState(null);
  const [verdict, setVerdict] = useState("Fetching Verdict...");
  const [loading, setLoading] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [searchInput, setSearchInput] = useState(""); // âœ… Added state for search input
  const [tollywoodMoviesData, setTollywoodMoviesData] = useState([]);
  const [bollywoodMoviesData, setBollywoodMoviesData] = useState([]);
  const [hollywoodMoviesData, setHollywoodMoviesData] = useState([]);
  const OMDB_API_KEY = process.env.REACT_APP_OMDB_API_KEY;

  const fetchMovie = (title) => {
    setLoading(true);
    console.log(`ðŸ” Fetching data for: ${title}`);

    axios
      .get(`http://127.0.0.1:4121/api/movies/search?title=${title}`)
      .then((res) => {
        console.log("âœ… Movie Data Received:", res.data);

        setMovie(res.data);
        setVerdict("Fetching Verdict...");
        fetchVerdict(res.data.title); // âœ… Fetch verdict

        setTimeout(() => setIsModalOpen(true), 200);
        setLoading(false);
      })
      .catch((err) => {
        console.error("âŒ Error fetching movie:", err);
        alert("Movie not found!");
        setLoading(false);
      });
  };

  const fetchVerdict = (movieTitle) => {
    axios
      .post("http://127.0.0.1:4121/api/sentiment/analyze", {
        reviews: ["This movie is amazing!", "Loved the storyline!"],
        movie_title: movieTitle,
      })
      .then((res) => {
        console.log("ðŸŽ¬ Verdict Received:", res.data.verdict);
        setVerdict(res.data.verdict);
      })
      .catch((err) => {
        console.error("âŒ Error fetching verdict:", err);
        setVerdict("Error fetching verdict");
      });
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setMovie(null);
    setVerdict("Fetching Verdict...");
    setSearchInput(""); // âœ… Clears search input after closing modal
  };

  useEffect(() => {
    const fetchMoviesData = async (movies) => {
      try {
        const movieRequests = movies.map((title) =>
          axios.get(`https://www.omdbapi.com/?t=${title}&apikey=${OMDB_API_KEY}`)
        );

        const responses = await Promise.all(movieRequests);

        return responses
          .filter((res) => res.data.Response === "True" && res.data.Poster !== "N/A")
          .map((res) => ({
            title: res.data.Title,
            year: res.data.Year,
            poster: res.data.Poster,
          }));
      } catch (error) {
        console.error("Error fetching movie data from OMDb:", error);
        return [];
      }
    };

    const loadMovies = async () => {
      setTollywoodMoviesData(await fetchMoviesData(tollywoodMovies.slice(0, 5)));
      setBollywoodMoviesData(await fetchMoviesData(bollywoodMovies.slice(0, 5)));
      setHollywoodMoviesData(await fetchMoviesData(hollywoodMovies.slice(0, 5)));
    };

    loadMovies();
  }, []);

  return (
    <div>
      <CustomNavbar />
      <MovieCarousel />

      {/* âœ… Search Bar with Bootstrap */}
      <Container className="mt-3">
        <SearchBar onSearch={fetchMovie} searchInput={searchInput} setSearchInput={setSearchInput} />
      </Container>

      {/* âœ… Header & Description Below Search Bar */}
      <Container className="text-center mt-3">
        <h2 className="text-gold">Where Quality & Clarity Matters</h2>
        <p className="text-light">
          Watch Tollywood, Bollywood, Hollywood movies in HD, exclusively available on iBommaFlix.
          Developed for smartphone, tablet, PC & TV.
        </p>
      </Container>

      {loading && <div className="spinner"></div>}

      {/* âœ… Movie Card Popup Modal with Bootstrap */}
      <Modal show={isModalOpen} onHide={closeModal} centered>
        <Modal.Header closeButton>
          <Modal.Title>Movie Details</Modal.Title>
        </Modal.Header>
        <Modal.Body className="text-center">
          {movie ? (
            <>
              <h3>{movie.title} ({movie.year})</h3>
              <img src={movie.poster} alt={movie.title} className="img-fluid rounded" />
              <p><strong>Genre:</strong> {movie.genre}</p>
              <p><strong>IMDb Rating:</strong> {movie.rating}</p>
              <p><strong>Plot:</strong> {movie.plot}</p>
              <h3 className="text-warning">{verdict}</h3>
            </>
          ) : (
            <p>Loading movie details...</p>
          )}
        </Modal.Body>
      </Modal>

      {/* âœ… Movie Grids Below Search Bar with Bootstrap */}
      <Container className="mt-4">
        <Row>
          <Col md={12}><h2 className="text-gold text-center">Tollywood Movies</h2></Col>
          {tollywoodMoviesData.map((movie, index) => (
            <Col key={index} xs={6} sm={4} md={2} className="text-center">
              <img src={movie.poster} alt={movie.title} className="img-fluid movie-poster" />
            </Col>
          ))}
        </Row>

        <Row className="mt-4">
          <Col md={12}><h2 className="text-gold text-center">Bollywood Movies</h2></Col>
          {bollywoodMoviesData.map((movie, index) => (
            <Col key={index} xs={6} sm={4} md={2} className="text-center">
              <img src={movie.poster} alt={movie.title} className="img-fluid movie-poster" />
            </Col>
          ))}
        </Row>

        <Row className="mt-4">
          <Col md={12}><h2 className="text-gold text-center">Hollywood Movies</h2></Col>
          {hollywoodMoviesData.map((movie, index) => (
            <Col key={index} xs={6} sm={4} md={2} className="text-center">
              <img src={movie.poster} alt={movie.title} className="img-fluid movie-poster" />
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default Home;
