import React, { useState, useEffect } from "react";
import axios from "axios";
import "./MovieCard.css";

const MovieCard = ({ movie }) => {
  const [verdict, setVerdict] = useState("");

  useEffect(() => {
    if (!movie) return; // Prevent unnecessary API calls

    axios
      .post("http://127.0.0.1:4121/api/sentiment/analyze", {
        reviews: [`This movie is amazing!`, `Loved the storyline!`],
        movie_title: movie.title
      })
      .then((res) => {
        setVerdict(res.data.verdict);
      })
      .catch((err) => console.error("Error fetching verdict:", err));
  }, [movie]); // Runs only when `movie` changes

  if (!movie) return null;

  return (
    <div className="movie-card">
      <div className="poster-container">
        <img src={movie.poster} alt={movie.title} className="movie-poster" />
      </div>
      <div className="movie-details">
        <h3>{movie.title} ({movie.year})</h3>
        <p><strong>Genre:</strong> {movie.genre}</p>
        <p><strong>IMDb Rating:</strong> {movie.rating}</p>
        <div className="plot-container">
          <p className="plot">{movie.plot}</p>
        </div>
        <h3 className="verdict" style={{ animation: "blink 1s infinite alternate" }}>
          {verdict}
        </h3>
      </div>
    </div>
  );
};

export default MovieCard;
