import React, { useState, useEffect } from "react";
import axios from "axios";
import Modal from "react-modal";
import CustomNavbar from "../components/Navbar";
import MovieCarousel from "../components/MovieCarousel";
import SearchBar from "../components/SearchBar";
import MovieCard from "../components/MovieCard";
import { tollywoodMovies, bollywoodMovies, hollywoodMovies } from "../data/movieList";
import "./Home.css";

// Set the root element for accessibility
Modal.setAppElement("#root");

const Home = () => {
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(false);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [tollywoodMoviesData, setTollywoodMoviesData] = useState([]);
  const [bollywoodMoviesData, setBollywoodMoviesData] = useState([]);
  const [hollywoodMoviesData, setHollywoodMoviesData] = useState([]);
  const OMDB_API_KEY = process.env.REACT_APP_OMDB_API_KEY;

  const fetchMovie = (title) => {
    setLoading(true);
    console.log(`ðŸ” Fetching data for: ${title}`);

    axios
      .get(`http://127.0.0.1:4121/api/movies/search?title=${title}`)
      .then((res) => {
        console.log("âœ… Movie Data Received:", res.data); // Debugging
        setMovie(res.data);
        setIsModalOpen(true); // âœ… Open modal after setting movie data
        setLoading(false);
      })
      .catch((err) => {
        console.error("âŒ Error fetching movie:", err);
        alert("Movie not found!");
        setLoading(false);
      });
  };

  useEffect(() => {
    console.log("ðŸŽ¬ Modal Open - Movie Data:", movie); // Debugging modal content

    const fetchMoviesData = async (movies) => {
      try {
        const movieRequests = movies.map((title) =>
          axios.get(`https://www.omdbapi.com/?t=${title}&apikey=${OMDB_API_KEY}`)
        );

        const responses = await Promise.all(movieRequests);

        return responses
          .filter((res) => res.data.Response === "True" && res.data.Poster !== "N/A")
          .map((res) => ({
            title: res.data.Title,
            year: res.data.Year,
            poster: res.data.Poster
          }));
      } catch (error) {
        console.error("âŒ Error fetching movie data from OMDb:", error);
        return [];
      }
    };

    const loadMovies = async () => {
      setTollywoodMoviesData(await fetchMoviesData(tollywoodMovies.slice(0, 5)));
      setBollywoodMoviesData(await fetchMoviesData(bollywoodMovies.slice(0, 5)));
      setHollywoodMoviesData(await fetchMoviesData(hollywoodMovies.slice(0, 5)));
    };

    loadMovies();
  }, []);

  return (
    <div>
      <CustomNavbar />
      <MovieCarousel />
      <SearchBar onSearch={fetchMovie} />

      {/* âœ… Header & Description Below Search Bar */}
      <div className="info-section">
        <h2 className="info-header">Where Quality & Clarity Matters</h2>
        <p className="info-text">
          Watch Tollywood, Bollywood, Hollywood movies in HD, exclusively available on iBommaFlix.
          Developed for smartphone, tablet, PC & TV.
        </p>
      </div>

      {loading && <div className="spinner"></div>}

      {/* âœ… Movie Card Popup Modal */}
      <Modal
        isOpen={isModalOpen}
        onRequestClose={() => setIsModalOpen(false)}
        className="movie-modal"
        overlayClassName="modal-overlay"
        contentLabel="Movie Details"
      >
        <button className="close-button" onClick={() => setIsModalOpen(false)}>âœ–</button>

        <div className="modal-content">
          {/* âœ… Debugging: Check if movie data exists */}
          {movie ? (
            <>
              <MovieCard movie={movie} />
            </>
          ) : (
            <p className="loading-text">Loading movie details...</p>
          )}
        </div>
      </Modal>
    </div>
  );
};

export default Home;
