import React, { useState, useEffect } from "react";
import axios from "axios";
import CustomNavbar from "../components/Navbar";
import MovieCarousel from "../components/MovieCarousel";
import SearchBar from "../components/SearchBar";
import MovieCard from "../components/MovieCard";
import { tollywoodMovies, bollywoodMovies, hollywoodMovies } from "../data/movieList";
import "./Home.css";

const Home = () => {
  const [movie, setMovie] = useState(null);
  const [loading, setLoading] = useState(false);
  const [tollywoodPosters, setTollywoodPosters] = useState([]);
  const [bollywoodPosters, setBollywoodPosters] = useState([]);
  const [hollywoodPosters, setHollywoodPosters] = useState([]);
  const OMDB_API_KEY = process.env.REACT_APP_OMDB_API_KEY;

  const fetchMovie = (title) => {
    setLoading(true);
    axios
      .get(`http://127.0.0.1:4121/api/movies/search?title=${title}`)
      .then((res) => {
        setMovie(res.data);
        setLoading(false);
      })
      .catch(() => {
        alert("Movie not found!");
        setLoading(false);
      });
  };

  useEffect(() => {
    const fetchPosters = async (movies) => {
      try {
        const posterRequests = movies.map((title) =>
          axios.get(`https://www.omdbapi.com/?t=${title}&apikey=${OMDB_API_KEY}`)
        );

        const responses = await Promise.all(posterRequests);

        return responses
          .filter((res) => res.data.Response === "True" && res.data.Poster !== "N/A")
          .map((res) => res.data.Poster);
      } catch (error) {
        console.error("Error fetching movie posters from OMDb:", error);
        return [];
      }
    };

    const loadPosters = async () => {
      setTollywoodPosters(await fetchPosters(tollywoodMovies.slice(0, 5)));
      setBollywoodPosters(await fetchPosters(bollywoodMovies.slice(0, 5)));
      setHollywoodPosters(await fetchPosters(hollywoodMovies.slice(0, 5)));
    };

    loadPosters();
  }, []);

  return (
    <div>
      <CustomNavbar />
      <MovieCarousel />
      <SearchBar onSearch={fetchMovie} />
      {loading && <div className="spinner"></div>}
      {movie && <MovieCard movie={movie} />}

      {/* Movie Grids Below Search Bar */}
      <div className="movie-grids">
        <div className="movie-grid">
          <h2 className="grid-title">Tollywood Movies</h2>
          <div className="movie-list">
            {tollywoodPosters.map((poster, index) => (
              <div key={index} className="movie-card">
                <img src={poster} alt="Movie Poster" className="movie-img" />
                <div className="overlay">
                  <button className="play-button">▶</button>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="movie-grid">
          <h2 className="grid-title">Bollywood Movies</h2>
          <div className="movie-list">
            {bollywoodPosters.map((poster, index) => (
              <div key={index} className="movie-card">
                <img src={poster} alt="Movie Poster" className="movie-img" />
                <div className="overlay">
                  <button className="play-button">▶</button>
                </div>
              </div>
            ))}
          </div>
        </div>

        <div className="movie-grid">
          <h2 className="grid-title">Hollywood Movies</h2>
          <div className="movie-list">
            {hollywoodPosters.map((poster, index) => (
              <div key={index} className="movie-card">
                <img src={poster} alt="Movie Poster" className="movie-img" />
                <div className="overlay">
                  <button className="play-button">▶</button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
